parameters.jsCode:
// Prepare embedding for the query
const message = $input.first().json.body.message;

// This will be replaced with actual embedding from OpenAI node
return {
  ...items[0].json,
  message_for_embedding: message
};

// Format retrieved documents as context for LLM
const results = $input.all();
const body = $('Webhook Legal Chat').first().json.body;

const contextChunks = results.map((item, index) => {
  const data = item.json;
  const metadata = typeof data.metadata === 'string' ? JSON.parse(data.metadata) : data.metadata;

  let sourceInfo = '';
  if (data.source === 'legal_library') {
    if (metadata.source_type === 'regulation') {
      sourceInfo = `[PRZEPIS] ${metadata.title || 'Przepis prawny'}`;
      if (metadata.article) sourceInfo += ` Art. ${metadata.article}`;
      if (metadata.paragraph) sourceInfo += ` § ${metadata.paragraph}`;
    } else if (metadata.source_type === 'ruling') {
      sourceInfo = `[ORZECZENIE] ${metadata.court_name || ''} - ${metadata.case_number || ''}`;
    } else {
      sourceInfo = `[${metadata.source_type?.toUpperCase() || 'DOKUMENT'}] ${metadata.title || ''}`;
    }
  } else {
    sourceInfo = `[DOKUMENT SPRAWY] ${metadata.title || metadata.filename || 'Dokument'}`;
  }

  return {
    index: index,
    source_type: data.source === 'legal_library' ? metadata.source_type : 'case_document',
    source_id: metadata.source_id || metadata.id || data.id,
    source_title: metadata.title || metadata.filename || 'Unknown',
    article: metadata.article || null,
    paragraph: metadata.paragraph || null,
    content: data.content,
    similarity: data.similarity,
    formatted: `--- Źródło [${index}]: ${sourceInfo} (similarity: ${(data.similarity * 100).toFixed(1)}%) ---\n${data.content}\n`
  };
});

const contextText = contextChunks.map(c => c.formatted).join('\n');

return {
  body: body,
  context: contextText,
  sources: contextChunks,
  sources_count: contextChunks.length
};

